// src/App.js
import React from "react";
import {
  useGetPokemonDetailsQuery,
  useGetPokemonsQuery,
} from "./services/sampleApi";

function PokemonList() {
  const { data, error, isLoading } = useGetPokemonsQuery();

  if (isLoading) return <p className="text-gray-700">Loading...</p>;
  if (error) return <p className="text-red-500">Error: {error.message}</p>;

  return (
    <ul className="text-gray-700">
      {data.results.map((pokemon) => (
        <PokemonListItem
          key={pokemon.name}
          name={pokemon.name}
          img={pokemon.url}
        />
      ))}
    </ul>
  );
}
function PokemonListItem({ name }) {
  const { data, error, isLoading } = useGetPokemonDetailsQuery(name);

  if (isLoading) return <p className="text-gray-700">Loading...</p>;
  if (error) return <p className="text-red-500">Error: {error.message}</p>;

  return (
    <li className="flex items-center space-x-4 p-2 border-b">
      <img src={data.sprites.front_default} alt={name} className="w-16 h-16" />
      <span>{name}</span>
    </li>
  );
}

function App() {
  return (
    <div className="App">
      <header className="bg-blue-500 text-white p-4">
        <h1 className="text-3xl font-bold">
          Welcome to React with Tailwind CSS and RTK Query
        </h1>
      </header>
      <main className="p-4">
        <PokemonList />
      </main>
    </div>
  );
}

export default App;
